function updateExamOptions() {
    const month = document.getElementById('exam_month').value;
    const examCentre = document.getElementById('exam_centre');
    const examBatch = document.getElementById('exam_batch');

    // Clear existing options
    examCentre.innerHTML = '';

    // Define options based on selected month
    let options = [];
    if (month === 'mar' || month === 'sep') {
        options = [
            { value: 'MUMBAI', text: 'Mumbai' },
            { value: 'DELHI', text: 'Delhi' },
            { value: 'KOLKATA', text: 'Kolkata' }
        ];
        examBatch.innerHTML = 'Batch for ' + (month === 'mar' ? 'March' : 'September');
    } else {
        options = [
            { value: 'MUMBAI', text: 'Mumbai' },
            { value: 'KOLKATA', text: 'Kolkata' },
            { value: 'CHENNAI', text: 'Chennai' },
            { value: 'DELHI', text: 'Delhi' },
            { value: 'KOCHI', text: 'Kochi' },
            { value: 'GOA', text: 'Goa' }
        ];
        examBatch.innerHTML = 'Batch for ' + (month === 'dec' ? 'December' : 'June');
    }

    // Populate the exam centre dropdown
    options.forEach(option => {
        const opt = document.createElement('option');
        opt.value = option.value;
        opt.textContent = option.text;
        examCentre.appendChild(opt);
    });
}
