template.script:
                                    
            function displayImage(input) {
                const cardImg = document.querySelector('.card-img');
                const file = input.files[0];

                if (file) {
                const reader = new FileReader();

                reader.onload = function (e) {
                    cardImg.src = e.target.result;
                };

                reader.readAsDataURL(file);
                }
            }

