                                    function checkAndOpenModal(button) {
                                            // Retrieve the data-id attribute value from the clicked button
                                            var dataId = button.getAttribute('data-id');
                                            var attendance_dropdown_value = document.getElementById("attendance_dropdown_" + dataId).value;

                                            if (attendance_dropdown_value == "") {
                                                alert("Attendance is Mandatory. Please Select Attendance");
                                                return;
                                            } else {
                                                openConfirmationModal(button);
                                            }
                                        }

                                    function openConfirmationModal(button){
                                           
                                            // Retrieve the data-id attribute value from the clicked button
                                            var dataId = button.getAttribute('data-id');
                                            var attendance_dropdown_value = document.getElementById("attendance_dropdown_"+dataId).value;


                                            if(attendance_dropdown_value == ""){
                                                alert("Attendance is Mandatory. Please Select Attendance");
                                                return ;
                                            }else{
                                                
                                            var subject = button.getAttribute('data-subject');
                                            var candidate_name = document.getElementById('candidate_name_'+dataId.toString()).innerText
                                            var roll_no = document.getElementById('candidate_roll_no_'+dataId.toString()).innerText
                                            document.getElementById('candidate_name_confirm_modal').innerText = candidate_name
                                            document.getElementById('modal_attendance').innerText =  attendance_dropdown_value
                                            document.getElementById('roll_no_confirm_modal').innerText = roll_no
                                            document.getElementById('confirm_modal_marksheet_id').value = dataId
                                            document.getElementById('confirm_modal_subject').value = subject
                                            document.getElementById('confirm_modal_attendance').value = attendance_dropdown_value
                                            $('#attendanceConfirmationModal').modal('show');

                                            

                                            }
                                            
                                    }
                                   



                                    

