function updateMarks(inputField, subjectArea) {
    var inputId = inputField.id;
    let candidate_id = inputId.match(/\d+$/)[0];
    let marks = parseFloat(inputField.value) || 0;

    var data = {
        candidate_id: candidate_id,
        subject_area: subjectArea,
        marks: marks
    };

    $.ajax({
        type: "POST",
        url: '/my/gpcandidate/update_marks',
        data: JSON.stringify(data),
        contentType: 'application/json',
        dataType: 'json',
        success: function (response) {
            console.log("POST request successful:", response);

            if (response.result === 'success') {
                // Recalculate total marks
                var subject1 = parseFloat($('#subject_area_1_2_3_' + candidate_id).val()) || 0;
                var subject2 = parseFloat($('#subject_area_4_5_6_' + candidate_id).val()) || 0;
                var subject3 = parseFloat($('#practical_record_journals_' + candidate_id).val()) || 0;
                var total = subject1 + subject2 + subject3;

                $('#gsk_oral_total_marks_' + candidate_id).text(total);

                // Update total marks in the backend
                var totalData = {
                    candidate_id: candidate_id,
                    gsk_oral_total_marks: total
                };

                $.ajax({
                    type: "POST",
                    url: '/my/gpcandidate/update_total_marks',
                    data: JSON.stringify(totalData),
                    contentType: 'application/json',
                    dataType: 'json',
                    success: function (response) {
                        console.log("Total marks updated successfully:", response);
                    },
                    error: function (xhr, status, error) {
                        console.error("Failed to update total marks:", error);
                    }
                });
            } else {
                console.error("Failed to update marks:", response.message);
            }
        },
        error: function (xhr, status, error) {
            console.error("POST request failed:", error);
        }
    });
}
