template.script:
            function openDayUpdateModal(button) {
            // Retrieve the data-id attribute value from the clicked button
            var dataId = button.getAttribute('assign-id');
            // Update the content of the modal with the retrieved data
            document.getElementById('assign_id').value = dataId;
            
            }

            function openEditTimeSheetModal(button) {
            // Retrieve the data-id attribute value from the clicked button
            var dataId = button.getAttribute('timesheet-id');
            // Update the content of the modal with the retrieved data
            document.getElementById('timesheet_id').value = dataId;
            
            }

        function checkExpense(expenseId, documentId) {
            const expenseInput = document.getElementById(expenseId);
            const documentInput = document.getElementById(documentId);
            
            if (expenseInput.value > 0) {
                documentInput.required = true;
            } else {
                documentInput.required = false;
            }
        }

        document.addEventListener('DOMContentLoaded', function () {
            // Get the current date and time
            let now = new Date();
            
            // Format the date and time for the datetime-local input (YYYY-MM-DDTHH:MM)
            let formattedDateTime = now.toISOString().slice(0, 16);
            
            // Set the input field to the current date and time
            document.getElementById('left_residence_date_time').value = formattedDateTime;
            document.getElementById('arrival_institute_hotel_date_time').value = formattedDateTime;
            document.getElementById('left_institute_date_time').value = formattedDateTime;
            document.getElementById('arrival_residence_date_time').value = formattedDateTime;
        });

function openDayUpdateModal(button) {
// Retrieve the data-id attribute value from the clicked button
var dataId = button.getAttribute('dgs-batch-id');
var instId = button.getAttribute('institute-id');
// Update the content of the modal with the retrieved data
document.getElementById('dgs_batch_id').value = dataId;
document.getElementById('institute_id').value = instId;
}
